const secs=document.querySelectorAll("section"),Menu=document.getElementById("navbar__list");function createMenuItems(){for(let e of secs)Menu.insertAdjacentHTML("beforeend",`<li><a class="menu__link" href="#${e.id}" data-nav="${e.id}">${e.dataset.nav}</a></li>`)}createMenuItems();let observer=new IntersectionObserver((e=>{e.forEach((e=>{let t=Menu.querySelector(`[data-nav=${e.target.id}]`);!0===e.isIntersecting?(e.target.classList.add("your-active-class"),t.classList.add("active")):(e.target.classList.remove("your-active-class"),t.classList.remove("active"))}))}),{threshold:.6});secs.forEach((e=>{observer.observe(e)}));const ButtonClick=document.querySelectorAll("#navbar__list li a");ButtonClick.forEach((e=>e.addEventListener("click",(function(e){e.preventDefault(),window.scrollTo({top:document.querySelector(e.currentTarget.getAttribute("href")).offsetTop,behavior:"smooth"})}))));